    and t.rel_id= #{relId}


    and t.b_id= #{bId}

mapper.select:
select count(1) count
from u_org_staff_rel t
where 1 =1

    and t.rel_id= #{relId}


    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.b_id= #{bId}


